import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const chatRef = useRef(null);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { text: input, sender: 'user' };
    setMessages(prev => [...prev, userMessage]);
    setInput('');

    try {
      const res = await axios.post('http://localhost:5000/api/chat', {
        message: input
      });

      const aiMessage = { text: res.data.response, sender: 'ai' };
      setMessages(prev => [...prev, aiMessage]);
    } catch (err) {
      setMessages(prev => [...prev, { text: 'Error: Unable to get response.', sender: 'ai' }]);
    }
  };

  const handleKeyDown = e => {
    if (e.key === 'Enter') sendMessage();
  };

  useEffect(() => {
    chatRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  return (
    <div style={{ maxWidth: 600, margin: "0 auto", padding: 20 }}>
      <div style={{ border: "1px solid #ccc", height: 400, overflowY: "auto", padding: 10 }}>
        {messages.map((msg, idx) => (
          <div
            key={idx}
            style={{
              margin: "10px 0",
              textAlign: msg.sender === 'user' ? 'right' : 'left'
            }}
          >
            <span style={{
              backgroundColor: msg.sender === 'user' ? '#007bff' : '#e4e6eb',
              color: msg.sender === 'user' ? '#fff' : '#000',
              padding: '10px 15px',
              borderRadius: 20,
              display: 'inline-block',
              maxWidth: '80%'
            }}>
              {msg.text}
            </span>
          </div>
        ))}
        <div ref={chatRef} />
      </div>

      <div style={{ display: 'flex', marginTop: 10 }}>
        <input
          type="text"
          placeholder="Type a message..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          style={{ flex: 1, padding: 10 }}
        />
        <button onClick={sendMessage} style={{ padding: "10px 20px", background: "#007bff", color: "#fff", border: "none" }}>
          Send
        </button>
      </div>
    </div>
  );
};

export default Chat;
