import { useState } from 'react';
import axios from 'axios';

function Chat() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMsg = { sender: 'user', text: input };
    setMessages(prev => [...prev, userMsg]);

    try {
      const res = await axios.post('http://localhost:5000/api/chat', { message: input });
      const botMsg = { sender: 'bot', text: res.data.reply };
      setMessages(prev => [...prev, botMsg]);
    } catch (err) {
      const errMsg = { sender: 'bot', text: "Something went wrong." };
      setMessages(prev => [...prev, errMsg]);
    }

    setInput('');
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Chat with AI</h2>
      <div style={{ height: '300px', overflowY: 'auto', border: '1px solid #ccc', padding: '10px', marginBottom: '10px' }}>
        {messages.map((msg, i) => (
          <p key={i}><strong>{msg.sender}:</strong> {msg.text}</p>
        ))}
      </div>
      <input
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
        placeholder="Type a message"
        style={{ width: '70%', padding: '8px' }}
      />
      <button onClick={sendMessage} style={{ padding: '8px', marginLeft: '10px' }}>Send</button>
    </div>
  );
}

export default Chat;
